      fs.writeFileSync(path.join(dest,"tiapp.xml"), new_tiapp_xml);
      // copy the bundle
      fs.writeFileSync(path.join(dest_resources, config.app_name.replace(/ /g,"_") + ".zip"),fs.readFileSync(config.bundle_file));

      logger.info("TiShadow app ready");
